<div class="container mt-5">
    <div class="row justify-content-center">
      <div class="col-md-6">
        <div class="card">
          <div class="card-header">
            <h4>Sign In</h4>
          </div>
          <div class="card-body">
            <form [formGroup]="signForm">
              <div class="form-group">
                <label for="username">Username</label>
                <input type="text" class="form-control" formControlName="Name" placeholder="Enter your username">
              </div>
              <div class="form-group">
                <label for="password">Password</label>
                <input type="password" class="form-control" formControlName="mobile" placeholder="Enter your password">
              </div>
              <button type="submit" class="btn btn-primary btn-block">Sign In</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
