import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { EmployeeService } from 'src/app/Service/employee.service';
@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent implements OnInit {
  signForm!:FormGroup;
  constructor(private fb: FormBuilder,private ui:EmployeeService){

  }
  ngOnInit(): void {
    this.formload();
  }
  formload(){
   return this.signForm=this.fb.group({
    username:[''],
    mobile:['']
   })
  }
  onsubmit(){
  this.ui.signin(this.signForm.value).subscribe((res:any)=>{
    const authToken = response.token;
  })
  }
}
<div class="container mt-5">
    <div class="row justify-content-center">
      <div class="col-md-6">
        <div class="card">
          <div class="card-header">
            <h4>Sign In</h4>
          </div>
          <div class="card-body">
            <form [formGroup]="signForm">
              <div class="form-group">
                <label for="username">Username</label>
                <input type="text" class="form-control" formControlName="username" placeholder="Enter your username">
              </div>
              <div class="form-group">
                <label for="password">Password</label>
                <input type="password" class="form-control" formControlName="mobile" placeholder="Enter your password">
              </div>
              <button type="submit" (click)="onsubmit()" class="btn btn-primary btn-block">Sign In</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
